/** @jsxImportSource @emotion/react */
import React, { useState } from "react";
import "./SliderMobile.scss";
import { useTranslation } from "react-i18next";
import { useMediaQuery } from "react-responsive";
import { jsx, css, Global, ClassNames } from "@emotion/react";
import styled from "@emotion/styled";
import imgage1 from './images/img-1.svg'
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';

export const SliderMobile = () => {
  const { t } = useTranslation();

  const handleDragStart = (e) => e.preventDefault();

  const MobileSlider = ({ children }) => {
    const isMobile = useMediaQuery({ maxWidth: 375 });
    return isMobile ? children : null;
  };

  const images = [
    <img src="./images/img-1.svg" onDragStart={handleDragStart} />,
    <img src="./images/img-2.svg" onDragStart={handleDragStart} />,
    <img src="./images/img-3.svg" onDragStart={handleDragStart} />,
    <img src="./images/img-4.svg" onDragStart={handleDragStart} />,
  ];


  return (
    <MobileSlider>
    <AliceCarousel mouseTracking items={images} />
    </MobileSlider>
  );
};

const SliderCSS = css`
  position: relative;
  height: 200px;
  width: 200px;
  margin: 0 auto;
  overflow: hidden;
`;
