const initialState = { promts: {} };
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_STATE_TO_PROMT':
            return {
                ...state,
                promts: {
                    currentElement: !action.payload.boolean
                }
                }

        default:
            return state;
    }
}
export default reducer;
