import "./PlatfromOrgChart.scss";
import {
  setStateToPromt 
} from "../../redux/actions.js";
import { connect } from "react-redux";
const autoBindReact = require("auto-bind/react");

class PlatfromOrgChart extends React.Component {
  constructor(props) {
    super(props);
    autoBindReact(this);
    this.state = {
      treePromt: false,
      barometerPromt: false,
      ennPromt: false,
      mbtiPromt: false,
    };
  }

  showPropmt(e, currentElement) {
    this.props.setStateToPromt(currentElement, treu)
    e.preventDefault();
    // if (currentElement === "tree") {
