/** @jsxImportSource @emotion/react */
import React, { useState } from "react";
import "./SliderMobile.scss";
import { useTranslation } from "react-i18next";
import { useMediaQuery } from "react-responsive";
import { jsx, css, Global, ClassNames } from "@emotion/react";
import styled from "@emotion/styled";
import SliderContent from "./SliderContent";
import Slide from "./Slide";

const slideImages = [
  require("./images/img-1.svg"),
  require("./images/img-2.svg"),
  require("./images/img-3.svg"),
  require("./images/img-4.svg"),
].map((m) => m.default);

export const Slideshow = (props) => {
  const { t } = useTranslation();

  const [state, setState] = useState({
    translate: 0,
    transition: 0.45,
  });

  const { translate, transition } = state;

  const [checkboxes, setValues] = useState({
    0: true,
    1: false,
    2: false,
    3: false,
  });

  const renderCheckbox = (i) => {
    return (
      <>
        <div
          onClick={() => onCheckboxClick(i)}
          className={"slider-checkbox " + (checkboxes[i] ? "active" : "")}
        >
          <div className="slider-checkbox__inner"></div>
        </div>
      </>
    );
  };

  const onCheckboxClick = (i) => {
    setValues({
      0: false,
      1: false,
      2: false,
      3: false,
      ...{
        [i]: true,
      },
    });

    setState({
        translate: (1 + 1)
    })
  };

  let currImg = Object.keys(checkboxes).find((key) => checkboxes[key] === true);

  return (
    <div css={SliderCSS}>
      <div className="checkboxes-wrapper">
        {renderCheckbox(0)}
        {renderCheckbox(1)}
        {renderCheckbox(2)}
        {renderCheckbox(3)}
      </div>
      <SliderContent translate={translate} transition={transition}>
        {slideImages.map((slide, i) => {
          if (i === Number(currImg)) {
            return <Slide key={slide + i} content={slide} i={i} />;
          }
        })}
      </SliderContent>
    </div>
  );
};

const SliderCSS = css`
  position: relative;
  height: 320px;
  width: 375px;
  margin: 0 auto;
  overflow: hidden;
`;
