import "./Header.scss";
import React, { useState, useRef } from "react";
import { useTranslation, initReactI18next } from "react-i18next";
import "../i18n";
import { useDetectOutsideClick } from "../utils/useDetectOutsideClick";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { ChangeLanguageButton } from '../changeLanguageButton/changeLanguageButton';

export const Header = () => {
  return (
    <div className="header">
      <div className="header-wrapper">
        <Link to="/">
          <div className="logo">
            <div className="logo-icon"></div>
          </div>
        </Link>
        <nav className="nav">
          <Link to="/platform">
            <div className="platform">{t("platform")}</div>
          </Link>
          <Link to="/about-app">
            <div className="about-app">{t("about-app")}</div>
          </Link>
          <Link to="/log-in">
            <div className="log-in">{t("log-in")}</div>
          </Link>
          <Link to="/get-started">
          <div className="get-started">{t("get-started")}</div>
          </Link>
          <ChangeLanguageButton />
        </nav>
      </div>
    </div>
  );
};
