import "./Header.scss";
import React from "react";
import { useTranslation } from "react-i18next";
import "../i18n";
import { Link } from "react-router-dom";
import { ChangeLanguageButton } from "../changeLanguageButton/changeLanguageButton";
import { connect } from "react-redux";
import { useMediaQuery } from 'react-responsive';

const Header = (props) => {
  const { t } = useTranslation();

  const isMobile = useMediaQuery({
    query: "(min-device-width: 375px)",
  });

  console.log(props.size);

  return (
    <>
      {isMobile && <p>You also have a huge screen</p>}
      {isMobile ? null : (
        <div className="header">
          <div className="header-wrapper">
            <Link to="/">
              <div className="logo">
                <div className="logo-icon"></div>
              </div>
            </Link>
            <nav className="nav">
              <Link to="/platform">
                <div className="platform">{t("platform")}</div>
              </Link>
              <Link to="/about-app">
                <div className="about-app">{t("about-app")}</div>
              </Link>
              <Link to="/study-page">
                <div className="study-page">{t("study-page")}</div>
              </Link>
              <Link to="/get-started">
                <div className="get-started">{t("get-started")}</div>
              </Link>
              <ChangeLanguageButton />
            </nav>
          </div>
        </div>
      )}
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    size: state.size,
  };
};

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
