import React from "react";
import "./PlatfromOrgChart.scss";
const autoBindReact = require("auto-bind/react");

export class PlatfromOrgChart extends React.Component {
  constructor(props) {
    super(props);
    autoBindReact(this);
    this.state = {
      treePromt: false,
      barometerPromt: false,
    };
  }

  showPropmt(e, currentElement) {
    e.preventDefault();
    if (currentElement === "tree") {
      if (!this.state.treePromt) {
        this.setState({
          treePromt: true,
        });
      } else {
        this.setState({
          treePromt: false,
        });
      }
    }
    if (currentElement === "barometer") {
      if (!this.state.barometerPromt) {
        this.setState({
          barometerPromt: true,
        });
      } else {
        this.setState({
          barometerPromt: false,
        });
      }
    }
  }

  renderPromt(state) {
    if (state.treePromt) {
      return (
        <div className="promt-tree promt-block">
          <div className="promt-block-tree-header">Adaptive Org-Chart</div>
          <div className="promt-block-text">Easy-to-use org-chart</div>
        </div>
      );
    }

    if (state.barometerPromt) {
      return (
        <div className="promt-barometer promt-charts">
          <div className="promt-block-charts-header">Barometer</div>
          <div className="promt-charts-text">Ð¡orporate climate</div>
        </div>
      );
    } else {
      return null;
    }
  }

  render() {
    console.log(this.state.treePromt);
    return (
      <>
        <div
          className="org-chart"
          onMouseEnter={(e) => {
            this.showPropmt(e, "tree");
          }}
          onMouseLeave={(e) => {
            this.showPropmt(e, "tree");
          }}
        ></div>

        {this.renderPromt(this.state)}

        <div className="charts">
          <div
            className="barometer"
            onMouseEnter={(e) => {
              this.showPropmt(e, "barometer");
            }}
            onMouseLeave={(e) => {
              this.showPropmt(e, "barometer");
            }}
          ></div>
          <div className="enn"></div>
          <div className="mbti"></div>
        </div>
      </>
    );
  }
}
