import "./Header.scss";
import React, { useState, useRef } from "react";
import { useTranslation, initReactI18next } from "react-i18next";
import "../i18n";
import { useDetectOutsideClick } from "../utils/useDetectOutsideClick";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

export const Header = () => {
  const { t, i18n } = useTranslation();

  const changeLanguage = (ln) => {
    i18n.changeLanguage(ln);
  };

  const DropDownMenu = () => {
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
    const toggle = () => setIsActive(!isActive);

    return (
      <div className="language">
        <div className="language-wrapper" onClick={toggle}>
          <div className="language-icon"></div>
          <div className="pick-language">{t("pick-language")}</div>
        </div>
        <div
          className={`drop-down ${isActive ? "active" : ""}`}
          ref={dropdownRef}
        >
          <div className="eng" onClick={() => changeLanguage("en")}>
            Engish
          </div>
          <div className="rus" onClick={() => changeLanguage("ru")}>
            Russian
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="header">
      <div className="header-wrapper">
      <Link to="/">
        <div className="logo">
          <div className="logo-icon"></div>
        </div>
        </Link>
        <nav className="nav">
        <Link to="/platform">
          <div className="platform">{t("platform")}</div>
          </Link>
          <Link to="about-app">
          <div className="about-app">{t("about-app")}</div>
          </Link>
          <div className="log-in">{t("log-in")}</div>
          <div className="get-started">{t("get-started")}</div>
          {DropDownMenu()}
        </nav>
      </div>
    </div>
  );
};
