import React from "react";
import "./PlatfromOrgChart.scss";
const autoBindReact = require("auto-bind/react");

export class PlatfromOrgChart extends React.Component {
  constructor(props) {
    super(props);
    autoBindReact(this);
    this.state = {
      eployeePropmt: false,
      treePromt: false
    };
  }

  showPropmt(e) {
    e.preventDefault();

    if (!this.state.eployeePropmt) {
      this.setState({
        eployeePropmt: true,
        treePromt: true
      });
    } else {
      this.setState({
        eployeePropmt: false,
        treePromt: false
      });
    }
  }
  render() {
    console.log(this.props)
    return (
      <>
        <div className="org-chart" onMouseEnter={(e) => {
                  this.showPropmt(e);
                }}
                onMouseLeave={(e) => {
                  this.showPropmt(e);
                }}></div>
        <div className="charts">
          <div className="barometer"></div>
          <div className="enn"></div>
          <div className="mbti"></div>
        </div>
      </>
    );
  }
}
