const initialState = { promts: {} };
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET':
            if(!action.payload.text) return state;
            else {
                return { text: action.payload.text }
            }
        default:
            return state;
    }
}
export default reducer;
