import "./PlatformSidebar.scss";
import React from "react";

export class PlatformSidebar extends React.Component {

  render() {
    return (
        <div className="sidebar-department" >
        <div
          className="toggle-menu"
        >
          <div

            className="sidebar-arrow"
          ></div>
          <div
            className="sidebar-department-name" 
          >
            {departmentsTree.name}
          </div>
        </div>
        {this.state.departments[departmentsTree.url] === true ? null : (
          <React.Fragment>
            {bossSnippet}
            {departmentsTree.users.map((user, inx) => (
              <div className="link-wrapper" >
                <Link
                  to={`/org-chart/${user.username}`}
                  className="link"
                  key={user.username}
                  onClick={this.setUnactiveState()}
                >
                  <div
                    className={`user-snippet-wrapper ${
                      this.props.selectedUser === user.username
                        ? "selected-user"
                        : ""
                    }`}
                    key={user.url}
                  >
                    <UserSnippet user={user} key={user.url} />
                  </div>
                </Link>

                <Link
                  to={`/users/${user.username}`}
                  className="link"
                  key={user.url + user.username}
                  onClick={this.setUnactiveState()}
                >
                  <div
                    className="profile-icon"
                    data-title="Перейти к профилю"
                    key={user.username + user.url}
                  >
                    <FontAwesomeIcon
                      icon={faAddressCard}
                      key={inx + user.username}
                    />
                  </div>
                </Link>
              </div>
            ))}
            {children}
          </React.Fragment>
        )}
      </div>
    );
  }
}
