import "./PlatformSidebar.scss";
import React from "react";
import { withTranslation } from "react-i18next";
const autoBindReact = require("auto-bind/react");

class PlatformSidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      eployeePropmt: true,
    };
    autoBindReact(this);
  }

  showPropmt(e) {
    e.preventDefault();
    if (!this.state.eployeePropmt) {
      this.setState({
        eployeePropmt: true,
      });
    } 
  }

  leave() {
    this.setState({
      eployeePropmt: false,
    });
  }

  renderUser(position) {
    return (
      <div className="user-snippet-wrapper">
        <div className="user-snippet-photo"></div>
        <div className="user-snippet-pos">{position}</div>
      </div>
    );
  }

  render() {
    const { t } = this.props;
    return (
      <>
        {this.state.eployeePropmt ? (
          <div className="promt-list-of-employers promt-block">
            <div className="promt-block-header">{t("ergonza-platform-employers-header")}</div>
            <div className="promt-block-text">
              Easy transfer of organizational staff
            </div>
          </div>
        ) : null}

        <div
          className={`sidebar-wrapper ${this.state.eployeePropmt ? 'removeHov' : ''}`}
          onMouseEnter={(e) => {
            this.showPropmt(e);
          }}
          onMouseLeave={(e) => {
            this.leave(e);
          }}
        >
          <div className="sidebar-department">
            <div className="department">
              <div className="toggle-arrow"></div>
              <div className="dep-name">Компания</div>
            </div>
            {this.renderUser("CEO")}
          </div>
          <div className="sidebar-department sidebar-department-2">
            <div className="department">
              <div className="toggle-arrow"></div>
              <div className="dep-name">Отдел</div>
            </div>
            {this.renderUser("Сотрудник")}
            {this.renderUser("Сотрудник")}
            {this.renderUser("Сотрудник")}
            {this.renderUser("Сотрудник")}
            {this.renderUser("Сотрудник")}
            {this.renderUser("Сотрудник")}
          </div>

          <div className="sidebar-department sidebar-department-2">
            <div className="department">
              <div className="toggle-arrow toggle-arrow-right"></div>
              <div className="dep-name">Отдел</div>
            </div>
          </div>
          <div className="sidebar-department sidebar-department-2">
            <div className="department">
              <div className="toggle-arrow toggle-arrow-right"></div>
              <div className="dep-name">Отдел</div>
            </div>
          </div>
          <div className="sidebar-department sidebar-department-2">
            <div className="department">
              <div className="toggle-arrow toggle-arrow-right"></div>
              <div className="dep-name">Отдел</div>
            </div>
          </div>
          <div className="sidebar-department sidebar-department-2">
            <div className="department">
              <div className="toggle-arrow toggle-arrow-right"></div>
              <div className="dep-name">Отдел</div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default withTranslation()(PlatformSidebar);
