import "../i18n";

export const setStateToPromt = (currentElement, boolean) => {
    return {
      type: "SET_STATE_TO_PROMT",
      payload: {
        currentElement,
        boolean
      },
    };
  };

  export const setLanguage = (lang) => {
    return {
      type: "SET_LANGUAGE",
      payload: {
        lang
      },
    };
  };
