const initialState = { promts: { 
    'tree': true,
} };
const reducer = (state = initialState, action) => {
    let a = action.payload.promts[currentElement]
    switch (action.type) {
        case 'SET_STATE_TO_PROMT':

            return {
                ...state,
                promts: {
                    ...state.promts,
                    [action.payload.currentElement]: !action.payload.promts[currentElement] 
                }
                }

        default:
            return state;
    }
}
export default reducer;
