import React from "react";
import './HowItWorksLayout.scss';
import HowItWorksStep1 from '../HowItWorksStep1/HowItWorksStep1';
import HowItWorksStep2 from '../HowItWorksStep2/HowItWorksStep2';
import HowItWorksStep3 from '../HowItWorksStep3/HowItWorksStep3';
import { withTranslation } from 'react-i18next';

class HowItWorksLayout extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      checkboxes: {
        1: true,
        2: false, 
        3: false,
      }
    }
  }

  renderCheckbox(i) {
    return (
      <>
      <div 
        onClick={this.onCheckboxClick.bind(this, i)} 
        className={"how-it-works-checkbox " + (this.state.checkboxes[i] ? 'active' : '')}>
        <div className="how-it-works-checkbox__inner"></div>
      </div>
      <div className={`how-it-works-checkbox-num num-${i} ` + (this.state.checkboxes[i] ? 'active' : '')}>{i}</div>
      </>
    )
  }

  onCheckboxClick(i) {
    this.setState({
      checkboxes: {
        1: false,
        2: false,
        3: false,
        ...{
          [i]: true
        }
      }
    })
  }

  render() {
    const { t } = this.props;
    return (
    <div className="how-it-works-layout">
        <div className="how-it-works-layout-wrapper">
        <div className="how-it-works-layout-header">{t("how-it-works-layout-header")}</div>
        </div>
        <div className="how-it-works-guide">
          <div className="how-it-works-guide-wrapper">
            {this.renderCheckbox(1)}
            {this.renderCheckbox(2)}
            {this.renderCheckbox(3)}
          </div>
        </div>
     {this.state.checkboxes[1] ? 
      <HowItWorksStep1 /> : null
     }
     {this.state.checkboxes[2] ? 
      <HowItWorksStep2 /> : null
     }
     {this.state.checkboxes[3] ? 
      <HowItWorksStep3 /> : null
     }
    </div>
    );
  }

}

export default withTranslation()(HowItWorksLayout);
