import React, { useRef } from "react";
import { useTranslation } from "react-i18next";
import "../i18n";
import { useDetectOutsideClick } from "../utils/useDetectOutsideClick";
import './changeLanguageButton.scss';
import { 
  Link,
  useLocation
} from "react-router-dom";

export const ChangeLanguageButton = () => {
  const { t, i18n } = useTranslation();

  const changeLanguage = (ln) => {
    i18n.changeLanguage(ln);
  };

  const DropDownMenu = () => {
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
    const toggle = () => setIsActive(!isActive);

    return (
      <div className="language">
        <div className="language-wrapper" onClick={toggle}>
          <div className="language-icon"></div>
          <div className="pick-language">{t("pick-language")}</div>
        </div>
        <div
          className={`drop-down ${isActive ? "active" : ""}`}
          ref={dropdownRef}
        >
        <Link to="/?lang=en" className="changeLang eng" onClick={() => changeLanguage("en")}>
          <span >
            Engish
          </span>
          </Link>
          <Link to="/?lang=ru" >
          <span className="changeLang rus"  onClick={() => changeLanguage("ru")}>
            Russian
          </span>
          </Link>
        </div>
      </div>
    );
  };

  return DropDownMenu()
};
