import "./Header.scss";
import React, { useState, useRef }  from "react";
import { useTranslation, initReactI18next } from "react-i18next";
import "../i18n";

export const Header = () => {

  const { t, i18n } = useTranslation();

  const changeLanguage = (ln) => {
      i18n.changeLanguage(ln);
  };


  let dropdownRef = useRef(null);
  let [isActive, setIsActive] = useState(false);
  let toggle = () => setIsActive(!isActive);

  const DropdownMenu = () => {

  }


  return (
    <div className="header">
      <div className="header-wrapper">
        <div className="logo">
          <div className="logo-icon"></div>
        </div>
        <nav className="nav">
          <div className="platform">{t("platform")}</div>
          <div className="about-app">{t("about-app")}</div>
          <div className="log-in">{t("log-in")}</div>
          <div className="get-started">{t("get-started")}</div>
          <div className="language">
            <div className="language-wrapper" onClick={() => toggle}>
              <div className="language-icon"></div>
              <div className="pick-language">{t("pick-language")}</div>
            </div>
            <div className={`drop-down ${isActive ? 'active' : ''}`} ref={dropdownRef}>
              <div className="en" onClick={() => changeLanguage("en")}>
                En
              </div>
              <div className="ru" onClick={() => changeLanguage("ru")}>
                Rus
              </div>
            </div>
          </div>
        </nav>
      </div>
    </div>
  );
};
