import React, { useRef } from "react";
import { useTranslation } from "react-i18next";
import "../i18n";
import { useDetectOutsideClick } from "../utils/useDetectOutsideClick";
import "./changeLanguageButton.scss";
import { useMediaQuery } from "react-responsive";

export const ChangeLanguageButton = () => {
  const { t, i18n } = useTranslation();

  const changeLanguage = (ln) => {
    i18n.changeLanguage(ln);
  };

  const DropDownMenu = () => {
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
    const toggle = () => setIsActive(!isActive);

    const MobileHeader = ({ children }) => {
      const isMobile = useMediaQuery({ maxWidth: 375 });
      return isMobile ? children : null;
    };

    const DefaultHeader = ({ children }) => {
      const isNotMobile = useMediaQuery({ minWidth: 376 });
      return isNotMobile ? children : null;
    };

    return (
      <>
      <MobileHeader></MobileHeader>

      <div className="language">
        <div className="language-wrapper" onClick={toggle}>
          <div className="language-icon"></div>
          <div className="pick-language">{t("pick-language")}</div>
        </div>
        <div
          className={`drop-down ${isActive ? "active" : ""}`}
          ref={dropdownRef}
        >
          <span

            className="changeLang eng"
            onClick={() => changeLanguage("en")}
          >
            Engish
          </span>

          <span className="changeLang rus" onClick={() => changeLanguage("ru")}>
            Russian
          </span>
        </div>
      </div>
      </>
    );
  };

  return DropDownMenu();
};
