/** @jsxImportSource @emotion/react */
import React, { useState } from "react";
import "./SliderMobile.scss";
import { useTranslation } from "react-i18next";
import { useMediaQuery } from "react-responsive";
import { jsx, css, Global, ClassNames } from "@emotion/react";
import styled from "@emotion/styled";
import SliderContent from './SliderContent'
import Slide from './Slide'


const slideImages = [
    require("./images/img-1.svg"),
    require("./images/img-2.svg"),
    require("./images/img-3.svg"),
    require("./images/img-4.svg")
].map(m => m.default);

export const  Slideshow = (props) => {
  const { t } = useTranslation();

  const width = 375;

  const [state, setState] = useState({
    translate: 0,
    transition: 0.45
  })

  const [checkboxes, setValues] = useState({
    1: true,
    2: false,
    3: false,
    4: false,
  });

  const renderCheckbox = (i) => {
    return (
      <>
        <div
          onClick={() => onCheckboxClick(i)}
          className={"how-it-works-checkbox " + (checkboxes[i] ? "active" : "")}
        >
          <div className="how-it-works-checkbox__inner"></div>
        </div>
        <div
          className={
            `how-it-works-checkbox-num-mobile num-${i}-mobile ` +
            (checkboxes[i] ? "active" : "")
          }
        >
          {i}
        </div>
      </>
    );
  };

  const onCheckboxClick = (i) => {
    setValues({
      1: false,
      2: false,
      3: false,
      4: false,
      ...{
        [i]: true,
      },
    });
  };



  const { translate, transition } = state

  return (
    <div css={SliderCSS}>
    <div className="checkboxes">
    {renderCheckbox(1)}
              {renderCheckbox(2)}
              {renderCheckbox(3)}
    </div>
    <SliderContent
        translate={translate}
        transition={transition}
        width={width * slideImages.length}
      >
         {slideImages.map((slide, i) => (
          <Slide key={slide + i} content={slide} />
        ))}
      </SliderContent>
      </div>
  );
};

const SliderCSS = css`
  position: relative;
  height: 320px;
  width: 375px;
  margin: 0 auto;
  overflow: hidden;
`
