import "./";

const initialState = {
  promts: {
    tree: false,
    barometer: false,
    mbti: false,
    enn: false,
    side: true,
  },
  lang: null
};

const reducer = (state = initialState, action) => {

  switch (action.type) {
    case "SET_STATE_TO_PROMT":
      return {
        ...state,
        promts: {
          [action.payload.currentElement]: action.payload.boolean,
        },
      };

      case "SET_LANGUAGE":

          return {
            ...state,
           lang: action.payload.lang
          }



    default:
      return state;
  }
};
export default reducer;
