import i18n from "i18next";
import { initReactI18next } from "react-i18next";

import translationEN from './locals/translationEN.json';
import translationRU from './locals/translationRU';

const setLocalStr = () => {
  localStorage.setItem('translationRU', 'ru');
  localStorage.setItem('translationEN', 'en');
}

const resources = {
    en: {
        translation: translationEN
    },
    ru: {
        translation: translationRU
    }
}

i18n
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources,
    lng: localStorage.getItem(setLocalStr) || "en",

    interpolation: {
      escapeValue: false
    }
  });


  // export default i18n;
