const initialState = { promts: { 
    'tree': true,
    [action.payloadcurrentElement]: false
} };
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_STATE_TO_PROMT':
            return {
                ...state,
                promts: {
                    ...state.promts,
                    [action.payload.currentElement]: !action.payload.boolean 
                }
                }

        default:
            return state;
    }
}
export default reducer;
