import "./App.scss";
import React from "react";
import { HomePage } from "./Homepage/Homepage";
import { useTranslation } from "react-i18next";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { HowItWorks } from "./HowItWorks/HowItWorks";
import CaseStudyPage from "./CaseStudyPage/CaseStudyPage";
import Platform from "./Platfrom/Platfrom";
import { setLanguage } from "./redux/actions.js";
import { connect } from "react-redux";
import "./i18n";

import i18n from "i18next";
import { initReactI18next } from "react-i18next";

import translationEN from './locals/translationEN.json';
import translationRU from './locals/translationRU';


function App(props) {
  const { i18n } = useTranslation();
  const lang = i18n.language;
  props.setLanguage(lang);
  console.log(props.lang);

  // i18n.changeLanguage(props.lang);

  const resources = {
    en: {
        translation: translationEN
    },
    ru: {
        translation: translationRU
    }
}

i18n
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources,
    lng: "en",

    interpolation: {
      escapeValue: false
    }
  });



  return (
    <Router>
      <div className={`wrapper ${props.lang}`}>
        <Route path="/" exact component={HomePage} />
        <Route path={`/?lang=${props.lang}`} exact component={HomePage} />
        <Route path="/about-app" exact component={HowItWorks} />
        <Route path="/study-page" exact component={CaseStudyPage} />
        <Route path="/platform" exact component={Platform} />
      </div>
    </Router>
  );
}

const mapStateToProps = (state) => {
  return {
    lang: state.lang,
  };
};

const mapDispatchToProps = {
  setLanguage,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
