export const setStateToPromt = (currentElement, boolean) => {
    return {
      type: "SET_STATE_TO_PROMT",
      payload: {
        currentElement,
        boolean
      },
    };
  };

  export const setLanguage = (lang, n) => {
    return {
      type: "SET_LANGUAGE",
      payload: {
        lang,
        n
      },
    };
  };
