import React from "react";
import "./PlatfromOrgChart.scss";
const autoBindReact = require("auto-bind/react");

export class PlatfromOrgChart extends React.Component {
  constructor(props) {
    super(props);
    autoBindReact(this);
    this.state = {
      treePromt: false,
      barometerPromt: false,
      ennPromt: false,
    };
  }

  showPropmt(e, currentElement) {
    console.log(currentElement)
    e.preventDefault();
    if (currentElement === "tree") {
      if (!this.state.treePromt) {
        this.setState({
          treePromt: true,
          barometerPromt: false,
          ennPromt: false,
        });
      }
    }
    if (currentElement === "barometer") {
      if (!this.state.barometerPromt) {
        this.setState({
          barometerPromt: true,
          treePromt: false,
          ennPromt: false,
        });
      }
      if (currentElement === "enn") {
        if (!this.state.ennPromt) {
          this.setState({
            ennPromt: true,
            barometerPromt: false,
            treePromt: false,
          });
        }
      }
    }
  }

  leave() {
    this.setState({
      treePromt: false,
      barometerPromt: false,
      ennPromt: false,
    });
  }

  render() {
    return (
      <>
        <div
          className="org-chart"
          onMouseEnter={(e) => {
            this.showPropmt(e, "tree");
          }}
          onMouseLeave={(e) => {
            this.leave();
          }}
        ></div>

        {this.state.treePromt ? (
          <div className="promt-tree promt-block">
            <div className="promt-block-tree-header">Adaptive Org-Chart</div>
            <div className="promt-block-text">Easy-to-use org-chart</div>
          </div>
        ) : null}

        {this.state.barometerPromt ? (
          <div className="promt-barometer promt-charts">
            <div className="promt-block-charts-header">Barometer</div>
            <div className="promt-charts-text">Ð¡orporate climate</div>
          </div>
        ) : null}

        {this.state.ennPromt ? (
          <div className="promt-enn promt-charts">
            <div className="promt-block-charts-header">Ennagram</div>
            <div className="promt-charts-text">Principals</div>
          </div>
        ) : null}

        <div className="charts">
          <div
            className="barometer"
            onMouseEnter={(e) => {
              this.showPropmt(e, "barometer");
            }}
            onMouseLeave={(e) => {
              this.leave();
            }}
          ></div>

          <div
            className="enn"
            onMouseEnter={(e) => {
              this.showPropmt(e, "enn");
            }}
            onMouseLeave={(e) => {
              this.leave();
            }}
          ></div>
          <div className="mbti"></div>
        </div>
      </>
    );
  }
}
